import React, { Component } from "react";
import "./App.css";
import person from "./img/person.jpg";
import * as ml5 from "ml5";

class App extends Component {
  state = {
    predictions: []
  };

  setPredictions = pred => {
    this.setState({
      predictions: pred
    });
  };
  classifyImg = () => {
    const classifier = ml5.imageClassifier("MobileNet", modelLoaded);
    function modelLoaded() {
      console.log("Model Loaded!");
    }
    const image = document.getElementById("image");
    //Make prediction
    classifier
      .predict(image, 5, function(err, results) {
        return results;
      })
      .then(results => {
        // Set the predictions in the state
        this.setPredictions(results);
      });
  };
  componentDidMount() {
    this.classifyImg();
  }

  render() {
    let predictions = <div className="loader" />;
    if (this.state.predictions.length > 0) {
      predictions = this.state.predictions.map((pred, i) => {
        let { className, probability } = pred;
        probability = Math.floor((probability * 10000) / 100 + "%");
        return (
          <div key={i + ""}>
            {i + 1}. Prediction: {className} at {probability}>{" "}
          </div>
        );
      });
    }
    return (
      <div className="App">
        <h1>Image classification </h1>
        <img src={person} id="image" width="400" alt="" />
        {predictions}
      </div>
    );
  }
}

export default App;
